goog.provide('BV.size');


/**
 * Returns a buffered size.
 * @param {BV.Size} size Size.
 * @param {number} buffer Buffer.
 * @param {BV.Size=} opt_size Optional reusable size array.
 * @return {BV.Size} The buffered size.
 */
BV.size.buffer = function(size, buffer, opt_size) {
  if (opt_size === undefined) {
    opt_size = [0, 0];
  }
  opt_size[0] = size[0] + 2 * buffer;
  opt_size[1] = size[1] + 2 * buffer;
  return opt_size;
};


/**
 * Determines if a size has a positive area.
 * @param {BV.Size} size The size to test.
 * @return {boolean} The size has a positive area.
 */
BV.size.hasArea = function(size) {
  return size[0] > 0 && size[1] > 0;
};


/**
 * Returns a size scaled by a ratio. The result will be an array of integers.
 * @param {BV.Size} size Size.
 * @param {number} ratio Ratio.
 * @param {BV.Size=} opt_size Optional reusable size array.
 * @return {BV.Size} The scaled size.
 */
BV.size.scale = function(size, ratio, opt_size) {
  if (opt_size === undefined) {
    opt_size = [0, 0];
  }
  opt_size[0] = (size[0] * ratio + 0.5) | 0;
  opt_size[1] = (size[1] * ratio + 0.5) | 0;
  return opt_size;
};


/**
 * Returns an `BV.Size` array for the passed in number (meaning: square) or
 * `BV.Size` array.
 * (meaning: non-square),
 * @param {number|BV.Size} size Width and height.
 * @param {BV.Size=} opt_size Optional reusable size array.
 * @return {BV.Size} Size.
 * @api
 */
BV.size.toSize = function(size, opt_size) {
  if (Array.isArray(size)) {
    return size;
  } else {
    if (opt_size === undefined) {
      opt_size = [size, size];
    } else {
      opt_size[0] = opt_size[1] = /** @type {number} */ (size);
    }
    return opt_size;
  }
};

