/**
 * @classdesc
 * Allows the user to rotate the map by clicking and dragging on the map,
 * normally combined with an {@link BV.events.condition} that limits
 * it to when the alt and shift keys are held down.
 *
 * This interaction is only supported for mouse devices.
 *
 * @constructor
 * @extends {BV.interaction.Pointer}
 * @param {olx.interaction.DragRotateOptions=} opt_options Options.
 * @api
 */
BV.interaction.DragRotate = function(opt_options) {

  var options = opt_options ? opt_options : {};

  BV.interaction.Pointer.call(this, {
    handleDownEvent: BV.interaction.DragRotate.handleDownEvent_,
    handleDragEvent: BV.interaction.DragRotate.handleDragEvent_,
    handleUpEvent: BV.interaction.DragRotate.handleUpEvent_
  });

  /**
   * @private
   * @type {BV.EventsConditionType}
   */
  this.condition_ = options.condition ?
    options.condition : BV.events.condition.altShiftKeysOnly;

  /**
   * @private
   * @type {number|undefined}
   */
  this.lastAngle_ = undefined;

  /**
   * @private
   * @type {number}
   */
  this.duration_ = options.duration !== undefined ? options.duration : 250;
};
BV.inherits(BV.interaction.DragRotate, BV.interaction.Pointer);


/**
 * @param {BV.MapBrowserPointerEvent} mapBrowserEvent Event.
 * @this {BV.interaction.DragRotate}
 * @private
 */
BV.interaction.DragRotate.handleDragEvent_ = function(mapBrowserEvent) {
  if (!BV.events.condition.mouseOnly(mapBrowserEvent)) {
    return;
  }

  var map = mapBrowserEvent.map;
  var view = map.getView();
  if (view.getConstraints().rotation === BV.RotationConstraint.disable) {
    return;
  }
  var size = map.getSize();
  var offset = mapBrowserEvent.pixel;
  var theta =
      Math.atan2(size[1] / 2 - offset[1], offset[0] - size[0] / 2);
  if (this.lastAngle_ !== undefined) {
    var delta = theta - this.lastAngle_;
    var rotation = view.getRotation();
    BV.interaction.Interaction.rotateWithoutConstraints(
        view, rotation - delta);
  }
  this.lastAngle_ = theta;
};


/**
 * @param {BV.MapBrowserPointerEvent} mapBrowserEvent Event.
 * @return {boolean} Stop drag sequence?
 * @this {BV.interaction.DragRotate}
 * @private
 */
BV.interaction.DragRotate.handleUpEvent_ = function(mapBrowserEvent) {
  if (!BV.events.condition.mouseOnly(mapBrowserEvent)) {
    return true;
  }

  var map = mapBrowserEvent.map;
  var view = map.getView();
  view.setHint(BV.ViewHint.INTERACTING, -1);
  var rotation = view.getRotation();
  BV.interaction.Interaction.rotate(view, rotation,
      undefined, this.duration_);
  return false;
};


/**
 * @param {BV.MapBrowserPointerEvent} mapBrowserEvent Event.
 * @return {boolean} Start drag sequence?
 * @this {BV.interaction.DragRotate}
 * @private
 */
BV.interaction.DragRotate.handleDownEvent_ = function(mapBrowserEvent) {
  if (!BV.events.condition.mouseOnly(mapBrowserEvent)) {
    return false;
  }

  if (BV.events.condition.mouseActionButton(mapBrowserEvent) &&
      this.condition_(mapBrowserEvent)) {
    var map = mapBrowserEvent.map;
    map.getView().setHint(BV.ViewHint.INTERACTING, 1);
    this.lastAngle_ = undefined;
    return true;
  } else {
    return false;
  }
};


/**
 * @inheritDoc
 */
BV.interaction.DragRotate.prototype.shouldStopEvent = BV.functions.FALSE;

