/**
 * @constructor
 * @extends {BV.Disposable}
 * @param {string} className CSS class name.
 */
BV.render.Box = function(className) {

  /**
   * @type {BV.geom.Polygon}
   * @private
   */
  this.geometry_ = null;

  /**
   * @type {HTMLDivElement}
   * @private
   */
  this.element_ = /** @type {HTMLDivElement} */ (document.createElement('div'));
  this.element_.style.position = 'absolute';
  this.element_.className = 'BV-box ' + className;

  /**
   * @private
   * @type {BV.PluggableMap}
   */
  this.map_ = null;

  /**
   * @private
   * @type {BV.Pixel}
   */
  this.startPixel_ = null;

  /**
   * @private
   * @type {BV.Pixel}
   */
  this.endPixel_ = null;

};
BV.inherits(BV.render.Box, BV.Disposable);


/**
 * @inheritDoc
 */
BV.render.Box.prototype.disposeInternal = function() {
  this.setMap(null);
};


/**
 * @private
 */
BV.render.Box.prototype.render_ = function() {
  var startPixel = this.startPixel_;
  var endPixel = this.endPixel_;
  var px = 'px';
  var style = this.element_.style;
  style.left = Math.min(startPixel[0], endPixel[0]) + px;
  style.top = Math.min(startPixel[1], endPixel[1]) + px;
  style.width = Math.abs(endPixel[0] - startPixel[0]) + px;
  style.height = Math.abs(endPixel[1] - startPixel[1]) + px;
};


/**
 * @param {BV.PluggableMap} map Map.
 */
BV.render.Box.prototype.setMap = function(map) {
  if (this.map_) {
    this.map_.getOverlayContainer().removeChild(this.element_);
    var style = this.element_.style;
    style.left = style.top = style.width = style.height = 'inherit';
  }
  this.map_ = map;
  if (this.map_) {
    this.map_.getOverlayContainer().appendChild(this.element_);
  }
};


/**
 * @param {BV.Pixel} startPixel Start pixel.
 * @param {BV.Pixel} endPixel End pixel.
 */
BV.render.Box.prototype.setPixels = function(startPixel, endPixel) {
  this.startPixel_ = startPixel;
  this.endPixel_ = endPixel;
  this.createOrUpdateGeometry();
  this.render_();
};


/**
 * Creates or updates the cached geometry.
 */
BV.render.Box.prototype.createOrUpdateGeometry = function() {
  var startPixel = this.startPixel_;
  var endPixel = this.endPixel_;
  var pixels = [
    startPixel,
    [startPixel[0], endPixel[1]],
    endPixel,
    [endPixel[0], startPixel[1]]
  ];
  var coordinates = pixels.map(this.map_.getCoordinateFromPixel, this.map_);
  // close the polygon
  coordinates[4] = coordinates[0].slice();
  if (!this.geometry_) {
    this.geometry_ = new BV.geom.Polygon([coordinates]);
  } else {
    this.geometry_.setCoordinates([coordinates]);
  }
};


/**
 * @return {BV.geom.Polygon} Geometry.
 */
BV.render.Box.prototype.getGeometry = function() {
  return this.geometry_;
};

