/**
 * The default condition for determining whether the boxend event
 * should fire.
 * @param {BV.MapBrowserEvent} mapBrowserEvent The originating MapBrowserEvent
 *     leading to the box end.
 * @param {BV.Pixel} startPixel The starting pixel of the box.
 * @param {BV.Pixel} endPixel The end pixel of the box.
 * @return {boolean} Whether or not the boxend condition should be fired.
 * @this {BV.interaction.DragBox}
 */
BV.interaction.DragBox.defaultBoxEndCondition = function(mapBrowserEvent, startPixel, endPixel) {
  var width = endPixel[0] - startPixel[0];
  var height = endPixel[1] - startPixel[1];
  return width * width + height * height >= this.minArea_;
};


/**
 * @param {BV.MapBrowserPointerEvent} mapBrowserEvent Event.
 * @this {BV.interaction.DragBox}
 * @private
 */
BV.interaction.DragBox.handleDragEvent_ = function(mapBrowserEvent) {
  if (!BV.events.condition.mouseOnly(mapBrowserEvent)) {
    return;
  }

  this.box_.setPixels(this.startPixel_, mapBrowserEvent.pixel);

  this.dispatchEvent(new BV.interaction.DragBox.Event(BV.interaction.DragBox.EventType_.BOXDRAG,
      mapBrowserEvent.coordinate, mapBrowserEvent));
};


/**
 * Returns geometry of last drawn box.
 * @return {BV.geom.Polygon} Geometry.
 * @api
 */
BV.interaction.DragBox.prototype.getGeometry = function() {
  return this.box_.getGeometry();
};


/**
 * To be overridden by child classes.
 * FIXME: use constructor option instead of relying on overriding.
 * @param {BV.MapBrowserEvent} mapBrowserEvent Map browser event.
 * @protected
 */
BV.interaction.DragBox.prototype.onBoxEnd = BV.nullFunction;


/**
 * @param {BV.MapBrowserPointerEvent} mapBrowserEvent Event.
 * @return {boolean} Stop drag sequence?
 * @this {BV.interaction.DragBox}
 * @private
 */
BV.interaction.DragBox.handleUpEvent_ = function(mapBrowserEvent) {
  if (!BV.events.condition.mouseOnly(mapBrowserEvent)) {
    return true;
  }

  this.box_.setMap(null);

  if (this.boxEndCondition_(mapBrowserEvent,
      this.startPixel_, mapBrowserEvent.pixel)) {
    this.onBoxEnd(mapBrowserEvent);
    this.dispatchEvent(new BV.interaction.DragBox.Event(BV.interaction.DragBox.EventType_.BOXEND,
        mapBrowserEvent.coordinate, mapBrowserEvent));
  }
  return false;
};


/**
 * @param {BV.MapBrowserPointerEvent} mapBrowserEvent Event.
 * @return {boolean} Start drag sequence?
 * @this {BV.interaction.DragBox}
 * @private
 */
BV.interaction.DragBox.handleDownEvent_ = function(mapBrowserEvent) {
  if (!BV.events.condition.mouseOnly(mapBrowserEvent)) {
    return false;
  }

  if (BV.events.condition.mouseActionButton(mapBrowserEvent) &&
      this.condition_(mapBrowserEvent)) {
    this.startPixel_ = mapBrowserEvent.pixel;
    this.box_.setMap(mapBrowserEvent.map);
    this.box_.setPixels(this.startPixel_, this.startPixel_);
    this.dispatchEvent(new BV.interaction.DragBox.Event(BV.interaction.DragBox.EventType_.BOXSTART,
        mapBrowserEvent.coordinate, mapBrowserEvent));
    return true;
  } else {
    return false;
  }
};


