  var transform = null;
  if (rotation !== 0) {
    var centerX = x + anchorX;
    var centerY = y + anchorY;
    transform = BV.transform.compose(localTransform,
        centerX, centerY, 1, 1, rotation, -centerX, -centerY);

    BV.extent.createOrUpdateEmpty(box);
    BV.extent.extendCoordinate(box, BV.transform.apply(localTransform, p1));
    BV.extent.extendCoordinate(box, BV.transform.apply(localTransform, p2));
    BV.extent.extendCoordinate(box, BV.transform.apply(localTransform, p3));
    BV.extent.extendCoordinate(box, BV.transform.apply(localTransform, p4));
  } else {
    BV.extent.createOrUpdate(boxX, boxY, boxX + boxW, boxY + boxH, box);
  }
  var canvas = context.canvas;
  var intersects = box[0] <= canvas.width && box[2] >= 0 && box[1] <= canvas.height && box[3] >= 0;
