  /**
   * @private
   * @type {string}
   */
  this.text_ = '';

  /**
   * @private
   * @type {number}
   */
  this.textOffsetX_ = 0;

  /**
   * @private
   * @type {number}
   */
  this.textOffsetY_ = 0;

  /**
   * @private
   * @type {boolean|undefined}
   */
  this.textRotateWithView_ = undefined;

  /**
   * @private
   * @type {number}
   */
  this.textRotation_ = 0;

  /**
   * @private
   * @type {?BV.CanvasFillState}
   */
  this.textFillState_ = null;

  /**
   * @type {Object.<string, BV.CanvasFillState>}
   */
  this.fillStates = {};

  /**
   * @private
   * @type {?BV.CanvasStrokeState}
   */
  this.textStrokeState_ = null;

  /**
   * @type {Object.<string, BV.CanvasStrokeState>}
   */
  this.strokeStates = {};

  /**
   * @private
   * @type {BV.CanvasTextState}
   */
  this.textState_ = /** @type {BV.CanvasTextState} */ ({});

  /**
   * @type {Object.<string, BV.CanvasTextState>}
   */
  this.textStates = {};

  /**
   * @private
   * @type {string}
   */
  this.textKey_ = '';

  /**
   * @private
   * @type {string}
   */
  this.fillKey_ = '';

  /**
   * @private
   * @type {string}
   */
  this.strokeKey_ = '';

