  if (event.beta !== null) {
    this.set(BV.DeviceOrientation.Property_.BETA,
        BV.math.toRadians(event.beta));
  }
  if (event.gamma !== null) {
    this.set(BV.DeviceOrientation.Property_.GAMMA,
        BV.math.toRadians(event.gamma));
  }
