/**
 * Default styles for editing features.
 * @return {Object.<BV.geom.GeometryType, Array.<BV.style.Style>>} Styles
 */
BV.style.Style.createDefaultEditing = function() {
  /** @type {Object.<BV.geom.GeometryType, Array.<BV.style.Style>>} */
  var styles = {};
  var white = [255, 255, 255, 1];
  var blue = [0, 153, 255, 1];
  var width = 3;
  styles[BV.geom.GeometryType.POLYGON] = [
    new BV.style.Style({
      fill: new BV.style.Fill({
        color: [255, 255, 255, 0.5]
      })
    })
  ];
  styles[BV.geom.GeometryType.MULTI_POLYGON] =
      styles[BV.geom.GeometryType.POLYGON];

  styles[BV.geom.GeometryType.LINE_STRING] = [
    new BV.style.Style({
      stroke: new BV.style.Stroke({
        color: white,
        width: width + 2
      })
    }),
    new BV.style.Style({
      stroke: new BV.style.Stroke({
        color: blue,
        width: width
      })
    })
  ];
  styles[BV.geom.GeometryType.MULTI_LINE_STRING] =
      styles[BV.geom.GeometryType.LINE_STRING];

  styles[BV.geom.GeometryType.CIRCLE] =
      styles[BV.geom.GeometryType.POLYGON].concat(
          styles[BV.geom.GeometryType.LINE_STRING]
      );


  styles[BV.geom.GeometryType.POINT] = [
    new BV.style.Style({
      image: new BV.style.Circle({
        radius: width * 2,
        fill: new BV.style.Fill({
          color: blue
        }),
        stroke: new BV.style.Stroke({
          color: white,
          width: width / 2
        })
      }),
      zIndex: Infinity
    })
  ];
  styles[BV.geom.GeometryType.MULTI_POINT] =
      styles[BV.geom.GeometryType.POINT];

  styles[BV.geom.GeometryType.GEOMETRY_COLLECTION] =
      styles[BV.geom.GeometryType.POLYGON].concat(
          styles[BV.geom.GeometryType.LINE_STRING],
          styles[BV.geom.GeometryType.POINT]
      );

  return styles;
};


