/**
 * @classdesc
 * Feature format for reading and writing data in the EsriJSON format.
 *
 * @constructor
 * @extends {BV.format.JSONFeature}
 * @param {olx.format.EsriJSONOptions=} opt_options Options.
 * @api
 */
BV.format.EsriJSON = function(opt_options) {

  var options = opt_options ? opt_options : {};

  BV.format.JSONFeature.call(this);

  /**
   * Name of the geometry attribute for features.
   * @type {string|undefined}
   * @private
   */
  this.geometryName_ = options.geometryName;

};
BV.inherits(BV.format.EsriJSON, BV.format.JSONFeature);


/**
 * @param {EsriJSONGeometry} object Object.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @private
 * @return {BV.geom.Geometry} Geometry.
 */
BV.format.EsriJSON.readGeometry_ = function(object, opt_options) {
  if (!object) {
    return null;
  }
  /** @type {BV.geom.GeometryType} */
  var type;
  if (typeof object.x === 'number' && typeof object.y === 'number') {
    type = BV.geom.GeometryType.POINT;
  } else if (object.points) {
    type = BV.geom.GeometryType.MULTI_POINT;
  } else if (object.paths) {
    if (object.paths.length === 1) {
      type = BV.geom.GeometryType.LINE_STRING;
    } else {
      type = BV.geom.GeometryType.MULTI_LINE_STRING;
    }
  } else if (object.rings) {
    var layout = BV.format.EsriJSON.getGeometryLayout_(object);
    var rings = BV.format.EsriJSON.convertRings_(object.rings, layout);
    object = /** @type {EsriJSONGeometry} */(BV.obj.assign({}, object));
    if (rings.length === 1) {
      type = BV.geom.GeometryType.POLYGON;
      object.rings = rings[0];
    } else {
      type = BV.geom.GeometryType.MULTI_POLYGON;
      object.rings = rings;
    }
  }
  var geometryReader = BV.format.EsriJSON.GEOMETRY_READERS_[type];
  return /** @type {BV.geom.Geometry} */ (
    BV.format.Feature.transformWithOptions(
        geometryReader(object), false, opt_options));
};


/**
 * Determines inner and outer rings.
 * Checks if any polygons in this array contain any other polygons in this
 * array. It is used for checking for holes.
 * Logic inspired by: https://github.com/Esri/terraformer-arcgis-parser
 * @param {Array.<!Array.<!Array.<number>>>} rings Rings.
 * @param {BV.geom.GeometryLayout} layout Geometry layout.
 * @private
 * @return {Array.<!Array.<!Array.<number>>>} Transformed rings.
 */
BV.format.EsriJSON.convertRings_ = function(rings, layout) {
  var flatRing = [];
  var outerRings = [];
  var holes = [];
  var i, ii;
  for (i = 0, ii = rings.length; i < ii; ++i) {
    flatRing.length = 0;
    BV.geom.flat.deflate.coordinates(flatRing, 0, rings[i], layout.length);
    // is this ring an outer ring? is it clockwise?
    var clockwise = BV.geom.flat.orient.linearRingIsClockwise(flatRing, 0,
        flatRing.length, layout.length);
    if (clockwise) {
      outerRings.push([rings[i]]);
    } else {
      holes.push(rings[i]);
    }
  }
  while (holes.length) {
    var hole = holes.shift();
    var matched = false;
    // loop over all outer rings and see if they contain our hole.
    for (i = outerRings.length - 1; i >= 0; i--) {
      var outerRing = outerRings[i][0];
      var containsHole = BV.extent.containsExtent(
          new BV.geom.LinearRing(outerRing).getExtent(),
          new BV.geom.LinearRing(hole).getExtent()
      );
      if (containsHole) {
        // the hole is contained push it into our polygon
        outerRings[i].push(hole);
        matched = true;
        break;
      }
    }
    if (!matched) {
      // no outer rings contain this hole turn it into and outer
      // ring (reverse it)
      outerRings.push([hole.reverse()]);
    }
  }
  return outerRings;
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.Geometry} Point.
 */
BV.format.EsriJSON.readPointGeometry_ = function(object) {
  var point;
  if (object.m !== undefined && object.z !== undefined) {
    point = new BV.geom.Point([object.x, object.y, object.z, object.m],
        BV.geom.GeometryLayout.XYZM);
  } else if (object.z !== undefined) {
    point = new BV.geom.Point([object.x, object.y, object.z],
        BV.geom.GeometryLayout.XYZ);
  } else if (object.m !== undefined) {
    point = new BV.geom.Point([object.x, object.y, object.m],
        BV.geom.GeometryLayout.XYM);
  } else {
    point = new BV.geom.Point([object.x, object.y]);
  }
  return point;
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.Geometry} LineString.
 */
BV.format.EsriJSON.readLineStringGeometry_ = function(object) {
  var layout = BV.format.EsriJSON.getGeometryLayout_(object);
  return new BV.geom.LineString(object.paths[0], layout);
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.Geometry} MultiLineString.
 */
BV.format.EsriJSON.readMultiLineStringGeometry_ = function(object) {
  var layout = BV.format.EsriJSON.getGeometryLayout_(object);
  return new BV.geom.MultiLineString(object.paths, layout);
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.GeometryLayout} The geometry layout to use.
 */
BV.format.EsriJSON.getGeometryLayout_ = function(object) {
  var layout = BV.geom.GeometryLayout.XY;
  if (object.hasZ === true && object.hasM === true) {
    layout = BV.geom.GeometryLayout.XYZM;
  } else if (object.hasZ === true) {
    layout = BV.geom.GeometryLayout.XYZ;
  } else if (object.hasM === true) {
    layout = BV.geom.GeometryLayout.XYM;
  }
  return layout;
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.Geometry} MultiPoint.
 */
BV.format.EsriJSON.readMultiPointGeometry_ = function(object) {
  var layout = BV.format.EsriJSON.getGeometryLayout_(object);
  return new BV.geom.MultiPoint(object.points, layout);
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.Geometry} MultiPolygon.
 */
BV.format.EsriJSON.readMultiPolygonGeometry_ = function(object) {
  var layout = BV.format.EsriJSON.getGeometryLayout_(object);
  return new BV.geom.MultiPolygon(
      /** @type {Array.<Array.<Array.<Array.<number>>>>} */(object.rings),
      layout);
};


/**
 * @param {EsriJSONGeometry} object Object.
 * @private
 * @return {BV.geom.Geometry} Polygon.
 */
BV.format.EsriJSON.readPolygonGeometry_ = function(object) {
  var layout = BV.format.EsriJSON.getGeometryLayout_(object);
  return new BV.geom.Polygon(object.rings, layout);
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONGeometry} EsriJSON geometry.
 */
BV.format.EsriJSON.writePointGeometry_ = function(geometry, opt_options) {
  var coordinates = /** @type {BV.geom.Point} */ (geometry).getCoordinates();
  var esriJSON;
  var layout = /** @type {BV.geom.Point} */ (geometry).getLayout();
  if (layout === BV.geom.GeometryLayout.XYZ) {
    esriJSON = /** @type {EsriJSONPoint} */ ({
      x: coordinates[0],
      y: coordinates[1],
      z: coordinates[2]
    });
  } else if (layout === BV.geom.GeometryLayout.XYM) {
    esriJSON = /** @type {EsriJSONPoint} */ ({
      x: coordinates[0],
      y: coordinates[1],
      m: coordinates[2]
    });
  } else if (layout === BV.geom.GeometryLayout.XYZM) {
    esriJSON = /** @type {EsriJSONPoint} */ ({
      x: coordinates[0],
      y: coordinates[1],
      z: coordinates[2],
      m: coordinates[3]
    });
  } else if (layout === BV.geom.GeometryLayout.XY) {
    esriJSON = /** @type {EsriJSONPoint} */ ({
      x: coordinates[0],
      y: coordinates[1]
    });
  } else {
    BV.asserts.assert(false, 34); // Invalid geometry layout
  }
  return /** @type {EsriJSONGeometry} */ (esriJSON);
};


/**
 * @param {BV.geom.SimpleGeometry} geometry Geometry.
 * @private
 * @return {Object} Object with boolean hasZ and hasM keys.
 */
BV.format.EsriJSON.getHasZM_ = function(geometry) {
  var layout = geometry.getLayout();
  return {
    hasZ: (layout === BV.geom.GeometryLayout.XYZ ||
      layout === BV.geom.GeometryLayout.XYZM),
    hasM: (layout === BV.geom.GeometryLayout.XYM ||
      layout === BV.geom.GeometryLayout.XYZM)
  };
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONPolyline} EsriJSON geometry.
 */
BV.format.EsriJSON.writeLineStringGeometry_ = function(geometry, opt_options) {
  var hasZM = BV.format.EsriJSON.getHasZM_(/** @type {BV.geom.LineString} */(geometry));
  return /** @type {EsriJSONPolyline} */ ({
    hasZ: hasZM.hasZ,
    hasM: hasZM.hasM,
    paths: [
      /** @type {BV.geom.LineString} */ (geometry).getCoordinates()
    ]
  });
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONPolygon} EsriJSON geometry.
 */
BV.format.EsriJSON.writePolygonGeometry_ = function(geometry, opt_options) {
  // Esri geometries use the left-hand rule
  var hasZM = BV.format.EsriJSON.getHasZM_(/** @type {BV.geom.Polygon} */(geometry));
  return /** @type {EsriJSONPolygon} */ ({
    hasZ: hasZM.hasZ,
    hasM: hasZM.hasM,
    rings: /** @type {BV.geom.Polygon} */ (geometry).getCoordinates(false)
  });
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONPolyline} EsriJSON geometry.
 */
BV.format.EsriJSON.writeMultiLineStringGeometry_ = function(geometry, opt_options) {
  var hasZM = BV.format.EsriJSON.getHasZM_(/** @type {BV.geom.MultiLineString} */(geometry));
  return /** @type {EsriJSONPolyline} */ ({
    hasZ: hasZM.hasZ,
    hasM: hasZM.hasM,
    paths: /** @type {BV.geom.MultiLineString} */ (geometry).getCoordinates()
  });
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONMultipoint} EsriJSON geometry.
 */
BV.format.EsriJSON.writeMultiPointGeometry_ = function(geometry, opt_options) {
  var hasZM = BV.format.EsriJSON.getHasZM_(/** @type {BV.geom.MultiPoint} */(geometry));
  return /** @type {EsriJSONMultipoint} */ ({
    hasZ: hasZM.hasZ,
    hasM: hasZM.hasM,
    points: /** @type {BV.geom.MultiPoint} */ (geometry).getCoordinates()
  });
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONPolygon} EsriJSON geometry.
 */
BV.format.EsriJSON.writeMultiPolygonGeometry_ = function(geometry,
    opt_options) {
  var hasZM = BV.format.EsriJSON.getHasZM_(/** @type {BV.geom.MultiPolygon} */(geometry));
  var coordinates = /** @type {BV.geom.MultiPolygon} */ (geometry).getCoordinates(false);
  var output = [];
  for (var i = 0; i < coordinates.length; i++) {
    for (var x = coordinates[i].length - 1; x >= 0; x--) {
      output.push(coordinates[i][x]);
    }
  }
  return /** @type {EsriJSONPolygon} */ ({
    hasZ: hasZM.hasZ,
    hasM: hasZM.hasM,
    rings: output
  });
};


/**
 * @const
 * @private
 * @type {Object.<BV.geom.GeometryType, function(EsriJSONGeometry): BV.geom.Geometry>}
 */
BV.format.EsriJSON.GEOMETRY_READERS_ = {};
BV.format.EsriJSON.GEOMETRY_READERS_[BV.geom.GeometryType.POINT] =
  BV.format.EsriJSON.readPointGeometry_;
BV.format.EsriJSON.GEOMETRY_READERS_[BV.geom.GeometryType.LINE_STRING] =
  BV.format.EsriJSON.readLineStringGeometry_;
BV.format.EsriJSON.GEOMETRY_READERS_[BV.geom.GeometryType.POLYGON] =
  BV.format.EsriJSON.readPolygonGeometry_;
BV.format.EsriJSON.GEOMETRY_READERS_[BV.geom.GeometryType.MULTI_POINT] =
  BV.format.EsriJSON.readMultiPointGeometry_;
BV.format.EsriJSON.GEOMETRY_READERS_[BV.geom.GeometryType.MULTI_LINE_STRING] =
  BV.format.EsriJSON.readMultiLineStringGeometry_;
BV.format.EsriJSON.GEOMETRY_READERS_[BV.geom.GeometryType.MULTI_POLYGON] =
  BV.format.EsriJSON.readMultiPolygonGeometry_;


/**
 * @const
 * @private
 * @type {Object.<string, function(BV.geom.Geometry, olx.format.WriteOptions=): (EsriJSONGeometry)>}
 */
BV.format.EsriJSON.GEOMETRY_WRITERS_ = {};
BV.format.EsriJSON.GEOMETRY_WRITERS_[BV.geom.GeometryType.POINT] =
  BV.format.EsriJSON.writePointGeometry_;
BV.format.EsriJSON.GEOMETRY_WRITERS_[BV.geom.GeometryType.LINE_STRING] =
  BV.format.EsriJSON.writeLineStringGeometry_;
BV.format.EsriJSON.GEOMETRY_WRITERS_[BV.geom.GeometryType.POLYGON] =
  BV.format.EsriJSON.writePolygonGeometry_;
BV.format.EsriJSON.GEOMETRY_WRITERS_[BV.geom.GeometryType.MULTI_POINT] =
  BV.format.EsriJSON.writeMultiPointGeometry_;
BV.format.EsriJSON.GEOMETRY_WRITERS_[BV.geom.GeometryType.MULTI_LINE_STRING] =
  BV.format.EsriJSON.writeMultiLineStringGeometry_;
BV.format.EsriJSON.GEOMETRY_WRITERS_[BV.geom.GeometryType.MULTI_POLYGON] =
  BV.format.EsriJSON.writeMultiPolygonGeometry_;


/**
 * Read a feature from a EsriJSON Feature source.  Only works for Feature,
 * use `readFeatures` to read FeatureCollection source.
 *
 * @function
 * @param {ArrayBuffer|Document|Node|Object|string} source Source.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @return {BV.Feature} Feature.
 * @api
 */
BV.format.EsriJSON.prototype.readFeature;


/**
 * Read all features from a EsriJSON source.  Works with both Feature and
 * FeatureCollection sources.
 *
 * @function
 * @param {ArrayBuffer|Document|Node|Object|string} source Source.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @return {Array.<BV.Feature>} Features.
 * @api
 */
BV.format.EsriJSON.prototype.readFeatures;


/**
 * @inheritDoc
 */
BV.format.EsriJSON.prototype.readFeatureFromObject = function(
    object, opt_options) {
  var esriJSONFeature = /** @type {EsriJSONFeature} */ (object);
  var geometry = BV.format.EsriJSON.readGeometry_(esriJSONFeature.geometry,
      opt_options);
  var feature = new BV.Feature();
  if (this.geometryName_) {
    feature.setGeometryName(this.geometryName_);
  }
  feature.setGeometry(geometry);
  if (opt_options && opt_options.idField &&
    esriJSONFeature.attributes[opt_options.idField]) {
    feature.setId(/** @type {number} */(
      esriJSONFeature.attributes[opt_options.idField]));
  }
  if (esriJSONFeature.attributes) {
    feature.setProperties(esriJSONFeature.attributes);
  }
  return feature;
};


/**
 * @inheritDoc
 */
BV.format.EsriJSON.prototype.readFeaturesFromObject = function(
    object, opt_options) {
  var esriJSONObject = /** @type {EsriJSONObject} */ (object);
  var options = opt_options ? opt_options : {};
  if (esriJSONObject.features) {
    var esriJSONFeatureCollection = /** @type {EsriJSONFeatureCollection} */
      (object);
    /** @type {Array.<BV.Feature>} */
    var features = [];
    var esriJSONFeatures = esriJSONFeatureCollection.features;
    var i, ii;
    options.idField = object.objectIdFieldName;
    for (i = 0, ii = esriJSONFeatures.length; i < ii; ++i) {
      features.push(this.readFeatureFromObject(esriJSONFeatures[i],
          options));
    }
    return features;
  } else {
    return [this.readFeatureFromObject(object, options)];
  }
};


/**
 * Read a geometry from a EsriJSON source.
 *
 * @function
 * @param {ArrayBuffer|Document|Node|Object|string} source Source.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @return {BV.geom.Geometry} Geometry.
 * @api
 */
BV.format.EsriJSON.prototype.readGeometry;


/**
 * @inheritDoc
 */
BV.format.EsriJSON.prototype.readGeometryFromObject = function(
    object, opt_options) {
  return BV.format.EsriJSON.readGeometry_(
      /** @type {EsriJSONGeometry} */(object), opt_options);
};


/**
 * Read the projection from a EsriJSON source.
 *
 * @function
 * @param {ArrayBuffer|Document|Node|Object|string} source Source.
 * @return {BV.proj.Projection} Projection.
 * @api
 */
BV.format.EsriJSON.prototype.readProjection;


/**
 * @inheritDoc
 */
BV.format.EsriJSON.prototype.readProjectionFromObject = function(object) {
  var esriJSONObject = /** @type {EsriJSONObject} */ (object);
  if (esriJSONObject.spatialReference && esriJSONObject.spatialReference.wkid) {
    var crs = esriJSONObject.spatialReference.wkid;
    return BV.proj.get('EPSG:' + crs);
  } else {
    return null;
  }
};


/**
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @private
 * @return {EsriJSONGeometry} EsriJSON geometry.
 */
BV.format.EsriJSON.writeGeometry_ = function(geometry, opt_options) {
  var geometryWriter = BV.format.EsriJSON.GEOMETRY_WRITERS_[geometry.getType()];
  return geometryWriter(/** @type {BV.geom.Geometry} */(
    BV.format.Feature.transformWithOptions(geometry, true, opt_options)),
  opt_options);
};


/**
 * Encode a geometry as a EsriJSON string.
 *
 * @function
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @return {string} EsriJSON.
 * @api
 */
BV.format.EsriJSON.prototype.writeGeometry;


/**
 * Encode a geometry as a EsriJSON object.
 *
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @return {EsriJSONGeometry} Object.
 * @override
 * @api
 */
BV.format.EsriJSON.prototype.writeGeometryObject = function(geometry,
    opt_options) {
  return BV.format.EsriJSON.writeGeometry_(geometry,
      this.adaptOptions(opt_options));
};


/**
 * Encode a feature as a EsriJSON Feature string.
 *
 * @function
 * @param {BV.Feature} feature Feature.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @return {string} EsriJSON.
 * @api
 */
BV.format.EsriJSON.prototype.writeFeature;


/**
 * Encode a feature as a esriJSON Feature object.
 *
 * @param {BV.Feature} feature Feature.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @return {Object} Object.
 * @override
 * @api
 */
BV.format.EsriJSON.prototype.writeFeatureObject = function(
    feature, opt_options) {
  opt_options = this.adaptOptions(opt_options);
  var object = {};
  var geometry = feature.getGeometry();
  if (geometry) {
    object['geometry'] =
      BV.format.EsriJSON.writeGeometry_(geometry, opt_options);
    if (opt_options && opt_options.featureProjection) {
      object['geometry']['spatialReference'] = /** @type {EsriJSONCRS} */({
        wkid: BV.proj.get(
            opt_options.featureProjection).getCode().split(':').pop()
      });
    }
  }
  var properties = feature.getProperties();
  delete properties[feature.getGeometryName()];
  if (!BV.obj.isEmpty(properties)) {
    object['attributes'] = properties;
  } else {
    object['attributes'] = {};
  }
  return object;
};


/**
 * Encode an array of features as EsriJSON.
 *
 * @function
 * @param {Array.<BV.Feature>} features Features.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @return {string} EsriJSON.
 * @api
 */
BV.format.EsriJSON.prototype.writeFeatures;


/**
 * Encode an array of features as a EsriJSON object.
 *
 * @param {Array.<BV.Feature>} features Features.
 * @param {olx.format.WriteOptions=} opt_options Write options.
 * @return {Object} EsriJSON Object.
 * @override
 * @api
 */
BV.format.EsriJSON.prototype.writeFeaturesObject = function(features, opt_options) {
  opt_options = this.adaptOptions(opt_options);
  var objects = [];
  var i, ii;
  for (i = 0, ii = features.length; i < ii; ++i) {
    objects.push(this.writeFeatureObject(features[i], opt_options));
  }
  return /** @type {EsriJSONFeatureCollection} */ ({
    'features': objects
  });
};

