/**
 * @param {Array.<BV.Feature>} features List of features.
 * @private
 */
BV.format.GPX.prototype.handleReadExtensions_ = function(features) {
  if (!features) {
    features = [];
  }
  for (var i = 0, ii = features.length; i < ii; ++i) {
    var feature = features[i];
    if (this.readExtensions_) {
      var extensionsNode = feature.get('extensionsNode_') || null;
      this.readExtensions_(feature, extensionsNode);
    }
    feature.set('extensionsNode_', undefined);
  }
};


/**
 * Read the first feature from a GPX source.
 * Routes (`<rte>`) are converted into LineString geometries, and tracks (`<trk>`)
 * into MultiLineString. Any properties on route and track waypoints are ignored.
 *
 * @function
 * @param {Document|Node|Object|string} source Source.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @return {BV.Feature} Feature.
 * @api
 */
BV.format.GPX.prototype.readFeature;


/**
 * @inheritDoc
 */
BV.format.GPX.prototype.readFeatureFromNode = function(node, opt_options) {
  if (!BV.array.includes(BV.format.GPX.NAMESPACE_URIS_, node.namespaceURI)) {
    return null;
  }
  var featureReader = BV.format.GPX.FEATURE_READER_[node.localName];
  if (!featureReader) {
    return null;
  }
  var feature = featureReader(node, [this.getReadOptions(node, opt_options)]);
  if (!feature) {
    return null;
  }
  this.handleReadExtensions_([feature]);
  return feature;
};


/**
 * Read all features from a GPX source.
 * Routes (`<rte>`) are converted into LineString geometries, and tracks (`<trk>`)
 * into MultiLineString. Any properties on route and track waypoints are ignored.
 *
 * @function
 * @param {Document|Node|Object|string} source Source.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @return {Array.<BV.Feature>} Features.
 * @api
 */
BV.format.GPX.prototype.readFeatures;


/**
 * @inheritDoc
 */
BV.format.GPX.prototype.readFeaturesFromNode = function(node, opt_options) {
  if (!BV.array.includes(BV.format.GPX.NAMESPACE_URIS_, node.namespaceURI)) {
    return [];
  }
  if (node.localName == 'gpx') {
    /** @type {Array.<BV.Feature>} */
    var features = BV.xml.pushParseAndPop([], BV.format.GPX.GPX_PARSERS_,
        node, [this.getReadOptions(node, opt_options)]);
    if (features) {
      this.handleReadExtensions_(features);
      return features;
    } else {
      return [];
    }
  }
  return [];
};


/**
 * Read the projection from a GPX source.
 *
 * @function
 * @param {Document|Node|Object|string} source Source.
 * @return {BV.proj.Projection} Projection.
 * @api
 */
BV.format.GPX.prototype.readProjection;


/**
 * @param {Node} node Node.
 * @param {string} value Value for the link's `href` attribute.
 * @param {Array.<*>} objectStack Node stack.
 * @private
 */
BV.format.GPX.writeLink_ = function(node, value, objectStack) {
  node.setAttribute('href', value);
  var context = objectStack[objectStack.length - 1];
  var properties = context['properties'];
  var link = [
    properties['linkText'],
    properties['linkType']
  ];
  BV.xml.pushSerializeAndPop(/** @type {BV.XmlNodeStackItem} */ ({node: node}),
      BV.format.GPX.LINK_SERIALIZERS_, BV.xml.OBJECT_PROPERTY_NODE_FACTORY,
      link, objectStack, BV.format.GPX.LINK_SEQUENCE_);
};


/**
 * @param {Node} node Node.
 * @param {BV.Coordinate} coordinate Coordinate.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.GPX.writeWptType_ = function(node, coordinate, objectStack) {
  var context = objectStack[objectStack.length - 1];
  var parentNode = context.node;
  var namespaceURI = parentNode.namespaceURI;
  var properties = context['properties'];
  //FIXME Projection handling
  BV.xml.setAttributeNS(node, null, 'lat', coordinate[1]);
  BV.xml.setAttributeNS(node, null, 'lon', coordinate[0]);
  var geometryLayout = context['geometryLayout'];
  switch (geometryLayout) {
    case BV.geom.GeometryLayout.XYZM:
      if (coordinate[3] !== 0) {
        properties['time'] = coordinate[3];
      }
      // fall through
    case BV.geom.GeometryLayout.XYZ:
      if (coordinate[2] !== 0) {
        properties['ele'] = coordinate[2];
      }
      break;
    case BV.geom.GeometryLayout.XYM:
      if (coordinate[2] !== 0) {
        properties['time'] = coordinate[2];
      }
      break;
    default:
      // pass
  }
  var orderedKeys = (node.nodeName == 'rtept') ?
    BV.format.GPX.RTEPT_TYPE_SEQUENCE_[namespaceURI] :
    BV.format.GPX.WPT_TYPE_SEQUENCE_[namespaceURI];
  var values = BV.xml.makeSequence(properties, orderedKeys);
  BV.xml.pushSerializeAndPop(/** @type {BV.XmlNodeStackItem} */
      ({node: node, 'properties': properties}),
      BV.format.GPX.WPT_TYPE_SERIALIZERS_, BV.xml.OBJECT_PROPERTY_NODE_FACTORY,
      values, objectStack, orderedKeys);
};


/**
 * @param {Node} node Node.
 * @param {BV.Feature} feature Feature.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.GPX.writeRte_ = function(node, feature, objectStack) {
  var options = /** @type {olx.format.WriteOptions} */ (objectStack[0]);
  var properties = feature.getProperties();
  var context = {node: node, 'properties': properties};
  var geometry = feature.getGeometry();
  if (geometry) {
    geometry = /** @type {BV.geom.LineString} */
      (BV.format.Feature.transformWithOptions(geometry, true, options));
    context['geometryLayout'] = geometry.getLayout();
    properties['rtept'] = geometry.getCoordinates();
  }
  var parentNode = objectStack[objectStack.length - 1].node;
  var orderedKeys = BV.format.GPX.RTE_SEQUENCE_[parentNode.namespaceURI];
  var values = BV.xml.makeSequence(properties, orderedKeys);
  BV.xml.pushSerializeAndPop(context,
      BV.format.GPX.RTE_SERIALIZERS_, BV.xml.OBJECT_PROPERTY_NODE_FACTORY,
      values, objectStack, orderedKeys);
};


/**
 * @param {Node} node Node.
 * @param {BV.Feature} feature Feature.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.GPX.writeTrk_ = function(node, feature, objectStack) {
  var options = /** @type {olx.format.WriteOptions} */ (objectStack[0]);
  var properties = feature.getProperties();
  /** @type {BV.XmlNodeStackItem} */
  var context = {node: node, 'properties': properties};
  var geometry = feature.getGeometry();
  if (geometry) {
    geometry = /** @type {BV.geom.MultiLineString} */
      (BV.format.Feature.transformWithOptions(geometry, true, options));
    properties['trkseg'] = geometry.getLineStrings();
  }
  var parentNode = objectStack[objectStack.length - 1].node;
  var orderedKeys = BV.format.GPX.TRK_SEQUENCE_[parentNode.namespaceURI];
  var values = BV.xml.makeSequence(properties, orderedKeys);
  BV.xml.pushSerializeAndPop(context,
      BV.format.GPX.TRK_SERIALIZERS_, BV.xml.OBJECT_PROPERTY_NODE_FACTORY,
      values, objectStack, orderedKeys);
};


