/**
 * @classdesc
 * Feature format for reading and writing data in the KML format.
 *
 * Note that the KML format uses the URL() constructor. Older browsers such as IE
 * which do not support this will need a URL polyfill to be loaded before use.
 *
 * @constructor
 * @extends {BV.format.XMLFeature}
 * @param {olx.format.KMLOptions=} opt_options Options.
 * @api
 */
BV.format.KML = function(opt_options) {

  var options = opt_options ? opt_options : {};

  BV.format.XMLFeature.call(this);

  if (!BV.format.KML.DEFAULT_STYLE_ARRAY_) {
    BV.format.KML.createStyleDefaults_();
  }

  /**
   * @inheritDoc
   */
  this.defaultDataProjection = BV.proj.get('EPSG:4326');

  /**
   * @private
   * @type {Array.<BV.style.Style>}
   */
  this.defaultStyle_ = options.defaultStyle ?
    options.defaultStyle : BV.format.KML.DEFAULT_STYLE_ARRAY_;

  /**
   * @private
   * @type {boolean}
   */
  this.extractStyles_ = options.extractStyles !== undefined ?
    options.extractStyles : true;

  /**
   * @private
   * @type {boolean}
   */
  this.writeStyles_ = options.writeStyles !== undefined ?
    options.writeStyles : true;

  /**
   * @private
   * @type {Object.<string, (Array.<BV.style.Style>|string)>}
   */
  this.sharedStyles_ = {};

  /**
   * @private
   * @type {boolean}
   */
  this.showPointNames_ = options.showPointNames !== undefined ?
    options.showPointNames : true;

};
BV.inherits(BV.format.KML, BV.format.XMLFeature);


