/**
 * @param {Node} node Node.
 * @param {BV.geom.Geometry} geometry Geometry.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.KML.writeMultiGeometry_ = function(node, geometry, objectStack) {
  /** @type {BV.XmlNodeStackItem} */
  var context = {node: node};
  var type = geometry.getType();
  /** @type {Array.<BV.geom.Geometry>} */
  var geometries;
  /** @type {function(*, Array.<*>, string=): (Node|undefined)} */
  var factory;
  if (type == BV.geom.GeometryType.GEOMETRY_COLLECTION) {
    geometries = /** @type {BV.geom.GeometryCollection} */ (geometry).getGeometries();
    factory = BV.format.KML.GEOMETRY_NODE_FACTORY_;
  } else if (type == BV.geom.GeometryType.MULTI_POINT) {
    geometries = /** @type {BV.geom.MultiPoint} */ (geometry).getPoints();
    factory = BV.format.KML.POINT_NODE_FACTORY_;
  } else if (type == BV.geom.GeometryType.MULTI_LINE_STRING) {
    geometries =
        (/** @type {BV.geom.MultiLineString} */ (geometry)).getLineStrings();
    factory = BV.format.KML.LINE_STRING_NODE_FACTORY_;
  } else if (type == BV.geom.GeometryType.MULTI_POLYGON) {
    geometries =
        (/** @type {BV.geom.MultiPolygon} */ (geometry)).getPolygons();
    factory = BV.format.KML.POLYGON_NODE_FACTORY_;
  } else {
    BV.asserts.assert(false, 39); // Unknown geometry type
  }
  BV.xml.pushSerializeAndPop(context,
      BV.format.KML.MULTI_GEOMETRY_SERIALIZERS_, factory,
      geometries, objectStack);
};


/**
 * @param {Node} node Node.
 * @param {BV.geom.LinearRing} linearRing Linear ring.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.KML.writeBoundaryIs_ = function(node, linearRing, objectStack) {
  var /** @type {BV.XmlNodeStackItem} */ context = {node: node};
  BV.xml.pushSerializeAndPop(context,
      BV.format.KML.BOUNDARY_IS_SERIALIZERS_,
      BV.format.KML.LINEAR_RING_NODE_FACTORY_, [linearRing], objectStack);
};


/**
 * FIXME currently we do serialize arbitrary/custom feature properties
 * (ExtendedData).
 * @param {Node} node Node.
 * @param {BV.Feature} feature Feature.
 * @param {Array.<*>} objectStack Object stack.
 * @this {BV.format.KML}
 * @private
 */
BV.format.KML.writePlacemark_ = function(node, feature, objectStack) {
  var /** @type {BV.XmlNodeStackItem} */ context = {node: node};

  // set id
  if (feature.getId()) {
    node.setAttribute('id', feature.getId());
  }

  // serialize properties (properties unknown to KML are not serialized)
  var properties = feature.getProperties();

  // don't export these to ExtendedData
  var filter = {'address': 1, 'description': 1, 'name': 1, 'open': 1,
    'phoneNumber': 1, 'styleUrl': 1, 'visibility': 1};
  filter[feature.getGeometryName()] = 1;
  var keys = Object.keys(properties || {}).sort().filter(function(v) {
    return !filter[v];
  });

  if (keys.length > 0) {
    var sequence = BV.xml.makeSequence(properties, keys);
    var namesAndValues = {names: keys, values: sequence};
    BV.xml.pushSerializeAndPop(context, BV.format.KML.PLACEMARK_SERIALIZERS_,
        BV.format.KML.EXTENDEDDATA_NODE_FACTORY_, [namesAndValues], objectStack);
  }

  var styleFunction = feature.getStyleFunction();
  if (styleFunction) {
    // FIXME the styles returned by the style function are supposed to be
    // resolution-independent here
    var styles = styleFunction.call(feature, 0);
    if (styles) {
      var style = Array.isArray(styles) ? styles[0] : styles;
      if (this.writeStyles_) {
        properties['Style'] = style;
      }
      var textStyle = style.getText();
      if (textStyle) {
        properties['name'] = textStyle.getText();
      }
    }
  }
  var parentNode = objectStack[objectStack.length - 1].node;
  var orderedKeys = BV.format.KML.PLACEMARK_SEQUENCE_[parentNode.namespaceURI];
  var values = BV.xml.makeSequence(properties, orderedKeys);
  BV.xml.pushSerializeAndPop(context, BV.format.KML.PLACEMARK_SERIALIZERS_,
      BV.xml.OBJECT_PROPERTY_NODE_FACTORY, values, objectStack, orderedKeys);

  // serialize geometry
  var options = /** @type {olx.format.WriteOptions} */ (objectStack[0]);
  var geometry = feature.getGeometry();
  if (geometry) {
    geometry =
        BV.format.Feature.transformWithOptions(geometry, true, options);
  }
  BV.xml.pushSerializeAndPop(context, BV.format.KML.PLACEMARK_SERIALIZERS_,
      BV.format.KML.GEOMETRY_NODE_FACTORY_, [geometry], objectStack);
};


/**
 * @param {Node} node Node.
 * @param {BV.geom.SimpleGeometry} geometry Geometry.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.KML.writePrimitiveGeometry_ = function(node, geometry, objectStack) {
  var flatCoordinates = geometry.getFlatCoordinates();
  var /** @type {BV.XmlNodeStackItem} */ context = {node: node};
  context['layout'] = geometry.getLayout();
  context['stride'] = geometry.getStride();

  // serialize properties (properties unknown to KML are not serialized)
  var properties = geometry.getProperties();
  properties.coordinates = flatCoordinates;

  var parentNode = objectStack[objectStack.length - 1].node;
  var orderedKeys = BV.format.KML.PRIMITIVE_GEOMETRY_SEQUENCE_[parentNode.namespaceURI];
  var values = BV.xml.makeSequence(properties, orderedKeys);
  BV.xml.pushSerializeAndPop(context, BV.format.KML.PRIMITIVE_GEOMETRY_SERIALIZERS_,
      BV.xml.OBJECT_PROPERTY_NODE_FACTORY, values, objectStack, orderedKeys);
};


/**
 * @param {Node} node Node.
 * @param {BV.geom.Polygon} polygon Polygon.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.KML.writePolygon_ = function(node, polygon, objectStack) {
  var linearRings = polygon.getLinearRings();
  var outerRing = linearRings.shift();
  var /** @type {BV.XmlNodeStackItem} */ context = {node: node};
  // inner rings
  BV.xml.pushSerializeAndPop(context,
      BV.format.KML.POLYGON_SERIALIZERS_,
      BV.format.KML.INNER_BOUNDARY_NODE_FACTORY_,
      linearRings, objectStack);
  // outer ring
  BV.xml.pushSerializeAndPop(context,
      BV.format.KML.POLYGON_SERIALIZERS_,
      BV.format.KML.OUTER_BOUNDARY_NODE_FACTORY_,
      [outerRing], objectStack);
};


/**
 * @param {Node} node Node.
 * @param {BV.style.Fill} style Style.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.KML.writePolyStyle_ = function(node, style, objectStack) {
  var /** @type {BV.XmlNodeStackItem} */ context = {node: node};
  BV.xml.pushSerializeAndPop(context, BV.format.KML.POLY_STYLE_SERIALIZERS_,
      BV.format.KML.COLOR_NODE_FACTORY_, [style.getColor()], objectStack);
};


/**
 * @param {Node} node Node to append a TextNode with the scale to.
 * @param {number|undefined} scale Scale.
 * @private
 */
BV.format.KML.writeScaleTextNode_ = function(node, scale) {
  // the Math is to remove any excess decimals created by float arithmetic
  BV.format.XSD.writeDecimalTextNode(node,
      Math.round(scale * 1e6) / 1e6);
};


/**
 * @param {Node} node Node.
 * @param {BV.style.Style} style Style.
 * @param {Array.<*>} objectStack Object stack.
 * @private
 */
BV.format.KML.writeStyle_ = function(node, style, objectStack) {
  var /** @type {BV.XmlNodeStackItem} */ context = {node: node};
  var properties = {};
  var fillStyle = style.getFill();
  var strokeStyle = style.getStroke();
  var imageStyle = style.getImage();
  var textStyle = style.getText();
  if (imageStyle instanceof BV.style.Icon) {
    properties['IconStyle'] = imageStyle;
  }
  if (textStyle) {
    properties['LabelStyle'] = textStyle;
  }
  if (strokeStyle) {
    properties['LineStyle'] = strokeStyle;
  }
  if (fillStyle) {
    properties['PolyStyle'] = fillStyle;
  }
  var parentNode = objectStack[objectStack.length - 1].node;
  var orderedKeys = BV.format.KML.STYLE_SEQUENCE_[parentNode.namespaceURI];
  var values = BV.xml.makeSequence(properties, orderedKeys);
  BV.xml.pushSerializeAndPop(context, BV.format.KML.STYLE_SERIALIZERS_,
      BV.xml.OBJECT_PROPERTY_NODE_FACTORY, values, objectStack, orderedKeys);
};


/**
 * @param {Node} node Node to append a TextNode with the Vec2 to.
 * @param {BV.KMLVec2_} vec2 Vec2.
 * @private
 */
BV.format.KML.writeVec2_ = function(node, vec2) {
  node.setAttribute('x', vec2.x);
  node.setAttribute('y', vec2.y);
  node.setAttribute('xunits', vec2.xunits);
  node.setAttribute('yunits', vec2.yunits);
};


