/**
 * Read all features from an OSM source.
 *
 * @function
 * @param {Document|Node|Object|string} source Source.
 * @param {olx.format.ReadOptions=} opt_options Read options.
 * @return {Array.<BV.Feature>} Features.
 * @api
 */
BV.format.OSMXML.prototype.readFeatures;


/**
 * @inheritDoc
 */
BV.format.OSMXML.prototype.readFeaturesFromNode = function(node, opt_options) {
  var options = this.getReadOptions(node, opt_options);
  if (node.localName == 'osm') {
    var state = BV.xml.pushParseAndPop({
      nodes: {},
      ways: [],
      features: []
    }, BV.format.OSMXML.PARSERS_, node, [options]);
    // parse nodes in ways
    for (var j = 0; j < state.ways.length; j++) {
      var values = /** @type {Object} */ (state.ways[j]);
      /** @type {Array.<number>} */
      var flatCoordinates = [];
      for (var i = 0, ii = values.ndrefs.length; i < ii; i++) {
        var point = state.nodes[values.ndrefs[i]];
        BV.array.extend(flatCoordinates, point);
      }
      var geometry;
      if (values.ndrefs[0] == values.ndrefs[values.ndrefs.length - 1]) {
        // closed way
        geometry = new BV.geom.Polygon(null);
        geometry.setFlatCoordinates(BV.geom.GeometryLayout.XY, flatCoordinates,
            [flatCoordinates.length]);
      } else {
        geometry = new BV.geom.LineString(null);
        geometry.setFlatCoordinates(BV.geom.GeometryLayout.XY, flatCoordinates);
      }
      BV.format.Feature.transformWithOptions(geometry, false, options);
      var feature = new BV.Feature(geometry);
      feature.setId(values.id);
      feature.setProperties(values.tags);
      state.features.push(feature);
    }
    if (state.features) {
      return state.features;
    }
  }
  return [];
};


