/**
 * @constructor
 * @extends {BV.Tile}
 * @param {BV.TileCoord} tileCoord Tile coordinate.
 * @param {BV.TileState} state State.
 * @param {string} src Image source URI.
 * @param {?string} crossOrigin Cross origin.
 * @param {BV.TileLoadFunctionType} tileLoadFunction Tile load function.
 * @param {olx.TileOptions=} opt_options Tile options.
 */
BV.ImageTile = function(tileCoord, state, src, crossOrigin, tileLoadFunction, opt_options) {

  BV.Tile.call(this, tileCoord, state, opt_options);

  /**
   * @private
   * @type {?string}
   */
  this.crossOrigin_ = crossOrigin;

  /**
   * Image URI
   *
   * @private
   * @type {string}
   */
  this.src_ = src;

  /**
   * @private
   * @type {Image|HTMLCanvasElement}
   */
  this.image_ = new Image();
  if (crossOrigin !== null) {
    this.image_.crossOrigin = crossOrigin;
  }

  /**
   * @private
   * @type {Array.<BV.EventsKey>}
   */
  this.imageListenerKeys_ = null;

  /**
   * @private
   * @type {BV.TileLoadFunctionType}
   */
  this.tileLoadFunction_ = tileLoadFunction;

};
BV.inherits(BV.ImageTile, BV.Tile);


/**
 * @inheritDoc
 */
BV.ImageTile.prototype.disposeInternal = function() {
  if (this.state == BV.TileState.LOADING) {
    this.unlistenImage_();
    this.image_ = BV.ImageTile.getBlankImage();
  }
  if (this.interimTile) {
    this.interimTile.dispose();
  }
  this.state = BV.TileState.ABORT;
  this.changed();
  BV.Tile.prototype.disposeInternal.call(this);
};


/**
 * Get the HTML image element for this tile (may be a Canvas, Image, or Video).
 * @return {HTMLCanvasElement|HTMLImageElement|HTMLVideoElement} Image.
 * @api
 */
BV.ImageTile.prototype.getImage = function() {
  return this.image_;
};


/**
 * @inheritDoc
 */
BV.ImageTile.prototype.getKey = function() {
  return this.src_;
};


/**
 * Tracks loading or read errors.
 *
 * @private
 */
BV.ImageTile.prototype.handleImageError_ = function() {
  this.state = BV.TileState.ERROR;
  this.unlistenImage_();
  this.image_ = BV.ImageTile.getBlankImage();
  this.changed();
};


/**
 * Tracks successful image load.
 *
 * @private
 */
BV.ImageTile.prototype.handleImageLoad_ = function() {
  if (this.image_.naturalWidth && this.image_.naturalHeight) {
    this.state = BV.TileState.LOADED;
  } else {
    this.state = BV.TileState.EMPTY;
  }
  this.unlistenImage_();
  this.changed();
};


/**
 * @inheritDoc
 * @api
 */
BV.ImageTile.prototype.load = function() {
  if (this.state == BV.TileState.ERROR) {
    this.state = BV.TileState.IDLE;
    this.image_ = new Image();
    if (this.crossOrigin_ !== null) {
      this.image_.crossOrigin = this.crossOrigin_;
    }
  }
  if (this.state == BV.TileState.IDLE) {
    this.state = BV.TileState.LOADING;
    this.changed();
    this.imageListenerKeys_ = [
      BV.events.listenOnce(this.image_, BV.events.EventType.ERROR,
          this.handleImageError_, this),
      BV.events.listenOnce(this.image_, BV.events.EventType.LOAD,
          this.handleImageLoad_, this)
    ];
    this.tileLoadFunction_(this, this.src_);
  }
};


/**
 * Discards event handlers which listen for load completion or errors.
 *
 * @private
 */
BV.ImageTile.prototype.unlistenImage_ = function() {
  this.imageListenerKeys_.forEach(BV.events.unlistenByKey);
  this.imageListenerKeys_ = null;
};


/**
 * Get a 1-pixel blank image.
 * @return {HTMLCanvasElement} Blank image.
 */
BV.ImageTile.getBlankImage = function() {
  var ctx = BV.dom.createCanvasContext2D(1, 1);
  ctx.fillStyle = 'rgba(0,0,0,0)';
  ctx.fillRect(0, 0, 1, 1);
  return ctx.canvas;
};

