goog.provide('BV.interaction.DragAndDrop');

goog.require('BV');
goog.require('BV.functions');
goog.require('BV.events');
goog.require('BV.events.Event');
goog.require('BV.events.EventType');
goog.require('BV.interaction.Interaction');
goog.require('BV.proj');


/**
 * @classdesc
 * Handles input of vector data by drag and drop.
 *
 * @constructor
 * @extends {BV.interaction.Interaction}
 * @fires BV.interaction.DragAndDrop.Event
 * @param {olx.interaction.DragAndDropOptions=} opt_options Options.
 * @api
 */
BV.interaction.DragAndDrop = function(opt_options) {

  var options = opt_options ? opt_options : {};

  BV.interaction.Interaction.call(this, {
    handleEvent: BV.interaction.DragAndDrop.handleEvent
  });

  /**
   * @private
   * @type {Array.<function(new: BV.format.Feature)>}
   */
  this.formatConstructors_ = options.formatConstructors ?
    options.formatConstructors : [];

  /**
   * @private
   * @type {BV.proj.Projection}
   */
  this.projection_ = options.projection ?
    BV.proj.get(options.projection) : null;

  /**
   * @private
   * @type {Array.<BV.EventsKey>}
   */
  this.dropListenKeys_ = null;

  /**
   * @private
   * @type {BV.source.Vector}
   */
  this.source_ = options.source || null;

  /**
   * @private
   * @type {Element}
   */
  this.target = options.target ? options.target : null;

};
BV.inherits(BV.interaction.DragAndDrop, BV.interaction.Interaction);


/**
 * @param {Event} event Event.
 * @this {BV.interaction.DragAndDrop}
 * @private
 */
BV.interaction.DragAndDrop.handleDrop_ = function(event) {
  var files = event.dataTransfer.files;
  var i, ii, file;
  for (i = 0, ii = files.length; i < ii; ++i) {
    file = files.item(i);
    var reader = new FileReader();
    reader.addEventListener(BV.events.EventType.LOAD,
        this.handleResult_.bind(this, file));
    reader.readAsText(file);
  }
};


/**
 * @param {Event} event Event.
 * @private
 */
BV.interaction.DragAndDrop.handleStop_ = function(event) {
  event.stopPropagation();
  event.preventDefault();
  event.dataTransfer.dropEffect = 'copy';
};


/**
 * @param {File} file File.
 * @param {Event} event Load event.
 * @private
 */
BV.interaction.DragAndDrop.prototype.handleResult_ = function(file, event) {
  var result = event.target.result;
  var map = this.getMap();
  var projection = this.projection_;
  if (!projection) {
    var view = map.getView();
    projection = view.getProjection();
  }

  var formatConstructors = this.formatConstructors_;
  var features = [];
  var i, ii;
  for (i = 0, ii = formatConstructors.length; i < ii; ++i) {
    /**
     * Avoid "cannot instantiate abstract class" error.
     * @type {Function}
     */
    var formatConstructor = formatConstructors[i];
    /**
     * @type {BV.format.Feature}
     */
    var format = new formatConstructor();
    features = this.tryReadFeatures_(format, result, {
      featureProjection: projection
    });
    if (features && features.length > 0) {
      break;
    }
  }
  if (this.source_) {
    this.source_.clear();
    this.source_.addFeatures(features);
  }
  this.dispatchEvent(
      new BV.interaction.DragAndDrop.Event(
          BV.interaction.DragAndDrop.EventType_.ADD_FEATURES, file,
          features, projection));
};


/**
 * Handles the {@link BV.MapBrowserEvent map browser event} unconditionally and
 * neither prevents the browser default nor stops event propagation.
 * @param {BV.MapBrowserEvent} mapBrowserEvent Map browser event.
 * @return {boolean} `false` to stop event propagation.
 * @this {BV.interaction.DragAndDrop}
 * @api
 */
BV.interaction.DragAndDrop.handleEvent = BV.functions.TRUE;


