  var options = opt_options ? opt_options : {};

  BV.interaction.Pointer.call(this, {
    handleDownEvent: BV.interaction.DragRotateAndZoom.handleDownEvent_,
    handleDragEvent: BV.interaction.DragRotateAndZoom.handleDragEvent_,
    handleUpEvent: BV.interaction.DragRotateAndZoom.handleUpEvent_
  });

  /**
   * @private
   * @type {BV.EventsConditionType}
   */
  this.condition_ = options.condition ?
    options.condition : BV.events.condition.shiftKeyOnly;

  /**
   * @private
   * @type {number|undefined}
   */
  this.lastAngle_ = undefined;

  /**
   * @private
   * @type {number|undefined}
   */
  this.lastMagnitude_ = undefined;

  /**
   * @private
   * @type {number}
   */
  this.lastScaleDelta_ = 0;

  /**
   * @private
   * @type {number}
   */
