/**
 * Strategy function for loading all features with a single request.
 * @param {BV.Extent} extent Extent.
 * @param {number} resolution Resolution.
 * @return {Array.<BV.Extent>} Extents.
 * @api
 */
BV.loadingstrategy.all = function(extent, resolution) {
  return [[-Infinity, -Infinity, Infinity, Infinity]];
};


/**
 * Strategy function for loading features based on the view's extent and
 * resolution.
 * @param {BV.Extent} extent Extent.
 * @param {number} resolution Resolution.
 * @return {Array.<BV.Extent>} Extents.
 * @api
 */
BV.loadingstrategy.bbox = function(extent, resolution) {
  return [extent];
};


/**
 * Creates a strategy function for loading features based on a tile grid.
 * @param {BV.tilegrid.TileGrid} tileGrid Tile grid.
 * @return {function(BV.Extent, number): Array.<BV.Extent>} Loading strategy.
 * @api
 */
BV.loadingstrategy.tile = function(tileGrid) {
  return (
  /**
       * @param {BV.Extent} extent Extent.
       * @param {number} resolution Resolution.
       * @return {Array.<BV.Extent>} Extents.
       */
    function(extent, resolution) {
      var z = tileGrid.getZForResolution(resolution);
      var tileRange = tileGrid.getTileRangeForExtentAndZ(extent, z);
      /** @type {Array.<BV.Extent>} */
      var extents = [];
      /** @type {BV.TileCoord} */
      var tileCoord = [z, 0, 0];
      for (tileCoord[1] = tileRange.minX; tileCoord[1] <= tileRange.maxX;
        ++tileCoord[1]) {
        for (tileCoord[2] = tileRange.minY; tileCoord[2] <= tileRange.maxY;
          ++tileCoord[2]) {
          extents.push(tileGrid.getTileCoordExtent(tileCoord));
        }
      }
      return extents;
    });
};

