/**
 * @param {olx.FrameState} frameState Frame state.
 * @param {BV.LayerState} layerState Layer state.
 * @param {BV.webgl.Context} context Context.
 */
BV.renderer.webgl.Layer.prototype.composeFrame = function(frameState, layerState, context) {

  this.dispatchComposeEvent_(
      BV.render.EventType.PRECOMPOSE, context, frameState);

  context.bindBuffer(BV.webgl.ARRAY_BUFFER, this.arrayBuffer_);

  var gl = context.getGL();

  var fragmentShader = BV.renderer.webgl.defaultmapshader.fragment;
  var vertexShader = BV.renderer.webgl.defaultmapshader.vertex;

  var program = context.getProgram(fragmentShader, vertexShader);

  var locations;
  if (!this.defaultLocations_) {
    locations = new BV.renderer.webgl.defaultmapshader.Locations(gl, program);
    this.defaultLocations_ = locations;
  } else {
    locations = this.defaultLocations_;
  }

  if (context.useProgram(program)) {
    gl.enableVertexAttribArray(locations.a_position);
    gl.vertexAttribPointer(
        locations.a_position, 2, BV.webgl.FLOAT, false, 16, 0);
    gl.enableVertexAttribArray(locations.a_texCoord);
    gl.vertexAttribPointer(
        locations.a_texCoord, 2, BV.webgl.FLOAT, false, 16, 8);
    gl.uniform1i(locations.u_texture, 0);
  }

  gl.uniformMatrix4fv(locations.u_texCoordMatrix, false,
      BV.vec.Mat4.fromTransform(this.tmpMat4_, this.getTexCoordMatrix()));
  gl.uniformMatrix4fv(locations.u_projectionMatrix, false,
      BV.vec.Mat4.fromTransform(this.tmpMat4_, this.getProjectionMatrix()));
  gl.uniform1f(locations.u_opacity, layerState.opacity);
  gl.bindTexture(BV.webgl.TEXTURE_2D, this.getTexture());
  gl.drawArrays(BV.webgl.TRIANGLE_STRIP, 0, 4);

  this.dispatchComposeEvent_(
      BV.render.EventType.POSTCOMPOSE, context, frameState);

};


