goog.require('BV');
goog.require('BV.structs.LRUCache');
goog.require('BV.tilecoord');


/**
 * @constructor
 * @extends {BV.structs.LRUCache.<BV.Tile>}
 * @param {number=} opt_highWaterMark High water mark.
 * @struct
 */
BV.TileCache = function(opt_highWaterMark) {

  BV.structs.LRUCache.call(this, opt_highWaterMark);

};
BV.inherits(BV.TileCache, BV.structs.LRUCache);


/**
 * @param {Object.<string, BV.TileRange>} usedTiles Used tiles.
 */
BV.TileCache.prototype.expireCache = function(usedTiles) {
  var tile, zKey;
  while (this.canExpireCache()) {
    tile = this.peekLast();
    zKey = tile.tileCoord[0].toString();
    if (zKey in usedTiles && usedTiles[zKey].contains(tile.tileCoord)) {
      break;
    } else {
      this.pop().dispose();
    }
  }
};


/**
 * Prune all tiles from the cache that don't have the same z as the newest tile.
 */
BV.TileCache.prototype.pruneExceptNewestZ = function() {
  if (this.getCount() === 0) {
    return;
  }
  var key = this.peekFirstKey();
  var tileCoord = BV.tilecoord.fromKey(key);
  var z = tileCoord[0];
  this.forEach(function(tile) {
    if (tile.tileCoord[0] !== z) {
      this.remove(BV.tilecoord.getKey(tile.tileCoord));
      tile.dispose();
    }
  }, this);
};

