BV.source.ImageVector.prototype.setStyle = function(style) {
  this.style_ = style !== undefined ? style : BV.style.Style.defaultFunction;
  this.styleFunction_ = !style ?
    undefined : BV.style.Style.createFunction(this.style_);
  this.changed();
};

